'use strict';

var averageModule = require('./average.js'),
    byNameModule = require('./by-name.js');

module.exports = ageDiff;

function ageDiff(ancestryArray) {
  var byName = byNameModule(ancestryArray);
  var motherAge = [];
  ancestryArray.forEach(function (person){
    if(byName[person.mother]){
      motherAge.push(person.born - byName[person.mother].born) ;
    }
  });
  return averageModule(motherAge);
}