'use strict';
var Sequence = require('./sequence.js');

module.exports = RangeSeq;

function RangeSeq (from, to) {
  return new Sequence( rangeAsArrayGenerator(from, to) );
}

function rangeAsArrayGenerator(from, to){
  //assume from and to are Numbers
  var result = [];
  if( from < to){
    while( from < to ){
      result.push(from)
      from++;
    }
    return result;
  }
}