'use strict';

module.exports = Sequence;

function Sequence(collectionOfValues){
  //assume Array of values
  this.collection = collectionOfValues;
  this.currentPosition = 0;
}
Sequence.prototype.iterate = function(){
  var currentValue = this.collection[this.currentPosition];
  this.currentPosition++;
  return currentValue;
};
Sequence.prototype.isEnd = function() {
  return this.currentPosition >= this.length;
};
Object.defineProperty(Sequence.prototype, 'length',{
  get: function(){
    return this.collection.length;
  }
});
