'use strict';

module.exports = sortBy;

function sortBy (array, sortKeyFunction) {
  var sorted = {};
  
  array.forEach(function (item){
    var sortKey = sortKeyFunction(item);
    if(!(sortKey in sorted)){
      sorted[sortKey] = [item];
    }
    else{
      sorted[sortKey].push(item);
    }
  });
  return sorted;
}