'use strict';

function StretchCell (inner, width, height) {
  this.innerCell = inner;
  this.scWidth = width;
  this.scHeight = height;
}

StretchCell.prototype.minWidth = function() {
  var innerWidth = this.innerCell.minWidth();
  return (innerWidth > this.scWidth) ? innerWidth: this.scWidth;
};
StretchCell.prototype.minHeight = function() {
  var innerHeight = this.innerCell.minHeight();
  return (innerHeight > this.scHeight) ? innerHeight : this.scHeight;
  
};
StretchCell.prototype.draw = function(width, height) {
  return this.innerCell.draw( this.minWidth(), this.minHeight() );
};
module.exports = StretchCell;